svg.script:


	"use strict";

	var svgElement = document.getElementById( "svg2" );
	var eyeScale = 1;


	function TransformPointByMatrix( point, matrix )
	{
		var result = {};
		result.x = point.x * matrix.a + point.y * matrix.c + matrix.e;
		result.y = point.x * matrix.b + point.y * matrix.d + matrix.f;

		return result;
	}

	function isIE () {
  var myNav = navigator.userAgent.toLowerCase();
  return (myNav.indexOf('msie') != -1) ? parseInt(myNav.split('msie')[1]) : false;
}

	function hsvToRgb(h, s, v) {
		var r, g, b;

		var i = Math.floor(h * 6);
		var f = h * 6 - i;
		var p = v * (1 - s);
		var q = v * (1 - f * s);
		var t = v * (1 - (1 - f) * s);

		switch (i % 6) {
			case 0: r = v, g = t, b = p; break;
			case 1: r = q, g = v, b = p; break;
			case 2: r = p, g = v, b = t; break;
			case 3: r = p, g = q, b = v; break;
			case 4: r = t, g = p, b = v; break;
			case 5: r = v, g = p, b = q; break;
		}

		return [ r * 255, g * 255, b * 255 ];
	}

	function rgbToHsv(r, g, b) {
		r /= 255, g /= 255, b /= 255;

		var max = Math.max(r, g, b), min = Math.min(r, g, b);
		var h, s, v = max;

		var d = max - min;
		s = max == 0 ? 0 : d / max;

		if (max == min) {
	h = 0; // achromatic
	} else {
		switch (max) {
			case r: h = (g - b) / d + (g < b ? 6 : 0); break;
			case g: h = (b - r) / d + 2; break;
			case b: h = (r - g) / d + 4; break;
		}

		h /= 6;
	}

	return [ h, s, v ];
	}

		function pad(s, size) {
		    while (s.length < size) s = "0" + s;
		    return s;
		}

		function ColorMultiply( col, mul )
		{
			col = parseInt( col, 16);
			var red = ( col >> 16 ) & 0x0000ff;
			var green = ( col >> 8 ) & 0x0000ff;
			var blue = ( col & 0x0000ff );

			var hsl = rgbToHsv( red, green, blue );

   			if ( hsl[2] > 0.25 )
   			{
				hsl[1] *= 1 + 0.5 * ( hsl[2] );
				hsl[2] *= 0.82;

				hsl[1] = Math.min( 1, hsl[1] );
			}
			else
			{
				hsl[2] += 0.3;

				hsl[1] = Math.min( 1, hsl[1] );
				hsl[2] = Math.min( 1, hsl[2] );
			}

			var rgb = hsvToRgb( hsl[0], hsl[1], hsl[2] );

			var new_col = ( rgb[0] << 16 ) + ( rgb[1] << 8 ) + ( parseInt( rgb[2] ) );

			return pad( new_col.toString( 16 ), 6 );
		}

		function ColorHSVShift( col, h, s, v )
		{
			col = parseInt( col, 16);
			var red = ( col >> 16 ) & 0x0000ff;
			var green = ( col >> 8 ) & 0x0000ff;
			var blue = ( col & 0x0000ff );

			var hsl = rgbToHsv( red, green, blue );

			hsl[0] = hsl[0] + h;
			if( hsl[0] < 0 ) { hsl[0] += 1 };
			if( hsl[0] > 1 ) { hsl[0] -= 1 };
			hsl[1] = Math.max( Math.min( hsl[1] + s, 1 ), 0 );
			hsl[2] = Math.max( Math.min( hsl[2] + v, 1 ), 0 );

			var rgb = hsvToRgb( hsl[0], hsl[1], hsl[2] );

			var new_col = ( rgb[0] << 16 ) + ( rgb[1] << 8 ) + ( parseInt( rgb[2] ) );

			return pad( new_col.toString( 16 ), 6 );
		}

		function SetIris( eye, azimuth, radius, scale )
		{
			var iris_mask = document.getElementById( "Eye" + eye + "IrisMask" );

			var point = svgElement.createSVGPoint();

			point.x = Math.cos( azimuth ) * radius * iris_mask.getAttribute( "rx" ) + parseInt( iris_mask.getAttribute( "cx" ) );
			point.y = -Math.sin( azimuth ) * radius * iris_mask.getAttribute( "ry" ) + parseInt( iris_mask.getAttribute( "cy" ) );

			// Get matrix
			var xforms = document.getElementById( "Eye" + eye + "IrisMask" ).transform.baseVal; 
			var firstXForm = xforms.getItem(0);    
			var matrix = firstXForm.matrix;		

			// Transform with ellipse matrix
			var new_point = point.matrixTransform( matrix );

			// Apply
			var iris = document.getElementById( "gIris" + eye );

			var new_matrix = svgElement.createSVGMatrix();

			var x_scale = 1;//0.8 + 0.2 * Math.abs( Math.sin( azimuth ) );
			var y_scale = 1;//0.8 + 0.2 * Math.abs( Math.cos( azimuth ) );

			if ( eye == "Back" )
			{
				x_scale *= 0.73;
			}

			new_matrix = new_matrix.translate( new_point.x, new_point.y ).scaleNonUniform( eyeScale * scale * x_scale, eyeScale * scale * y_scale );

			iris.transform.baseVal.clear( );;
			iris.transform.baseVal.appendItem( svgElement.createSVGTransformFromMatrix( new_matrix ) );
		}

		var t = 0;
		var timeout = null;

		function EyesShifty( )
		{
			t += 33.0 / 1000.0;

			var tt = t * 5;
			var sin = Math.sin(tt);

			if ( sin > 0 )
			{
				sin = 1 - Math.pow( 1 - sin, 3 );
			}
			else
			{
				sin = -( 1 - Math.pow( 1 - (-sin), 3 ) );
			}

			sin *= 0.8;

			SetIris( "Front", 0, sin, 1 );
			SetIris( "Back", 0, sin, 0.85 );
		}

		function EyesRoll( )
		{
			t += 33.0 / 1000.0;

			var tt = t * 5;

			tt /= 3.14159 * 2;

			tt = tt % 1.0;

			tt = Math.min( 1, tt * 2.0 );

			tt *= 3.14159 * 2;

			tt = 4 - tt;

			SetIris( "Front", tt, 0.5, 1 );
			SetIris( "Back", tt, 0.5, 0.85 );
		}

		function ProcessHash( hash )
		{
			//
			// Process new setup
			//

			var eye_1 = null;
			var eye_2 = null;

			var hairColor = null;
			var hairColorOutline = null;

			var hash_array = hash.substring( 1 ).split( ";" );

			var color = hash_array[ 0 ];

			var params = [];

			params[ "mouth" ] = 1;
			params[ "eye" ] = 2;
			params[ "mane" ] = 1;

			params[ "irisR" ] = 0.5;
			params[ "irisA" ] = 195;
			params[ "irisS" ] = 0.8;

			for ( var i = 1; i < hash_array.length; i++ )
			{
				var parameter_string = hash_array[ i ];
				var parameter_array = parameter_string.split( "=" );

				var key = parameter_array[ 0 ];
				var value = parameter_array[ 1 ];

				params[ key ] = value;				
			}			

			if ( typeof params[ "eyeTop" ] !== 'undefined' )
			{
				eye_1 = params[ "eyeTop" ];
			}

			if ( typeof params[ "eyeBottom" ] !== 'undefined' )
			{
				eye_2 = params[ "eyeBottom" ];
			}

			if ( typeof params[ "hairColor" ] !== 'undefined' )
			{
				hairColor = params[ "hairColor" ];
				hairColorOutline = ColorMultiply( params[ "hairColor" ], 1 );
			}		

			if ( color.length == 6 )
			{
				var outline_color = ColorMultiply( color, 0.7 );

				// Twilight
				if ( color == "d4a4e8" )
				{
					eye_1 = "290a4b";
					eye_2 = "633183";
					outline_color = "a66ebe";
				}

				// Applejack
				if ( color == "faba61" )
				{
					eye_1 = "267716";
					eye_2 = "60bd50";
					outline_color = "e97135";
				}

				// Pinkie
				if ( color == "f9b8d2" )
				{
					eye_1 = "187195";
					eye_2 = "7fd2f4";
					outline_color = "ea80b0";
				}

				// Dash
				if ( color == "9dd9f8" )
				{
					eye_1 = "570030";
					eye_2 = "c6006f";
					outline_color = "6aaadd";
				}

				// Rarity
				if ( color == "f0f2f3" )
				{
					eye_1 = "1a466a";
					eye_2 = "3777be";
					outline_color = "c2c5c6";
				}

				// Fluttershy
				if ( color == "fff798" )
				{
					eye_1 = "0a5251";
					eye_2 = "0fb0aa";
					outline_color = "e6b91f";
				}

				var css = "\
					.body-fill {\
			           fill:   #" + color + ";\
			        }\
\
			        .body-stroke {\
			           stroke:   #" + color + ";\
			        }\
\
			        .outline-fill {\
			           fill:   #" + outline_color + ";\
			        }\
\
			        .outline-stroke {\
			           stroke:   #" + outline_color + ";\
			        }\
			        ";

			    if ( hairColor != null )
			    {
			    css += "\
					.hair-base-fill {\
			           fill:   #" + hairColor + ";\
			        }\
\
			        .hair-base-stroke {\
			           stroke:   #" + hairColor + ";\
			        }\
\
			        .hair-outline-fill {\
			           fill:   #" + hairColorOutline + ";\
			        }\
\
			        .hair-outline-stroke {\
			           stroke:   #" + hairColorOutline + ";\
			        }\
			        ";
			    }

				if ( eye_1 != null )
				{
					css += ".sEyeTop { stop-color:#" + eye_1 + "; } ";
					css += ".sEyeBottom { stop-color:#" + eye_2 + "; } ";

					var gradientEye = document.getElementById( "gradientEye" );
					var gradientEyeParent = gradientEye.parentNode;
					gradientEyeParent.removeChild( gradientEye );
					gradientEyeParent.appendChild( gradientEye );

					/*document.getElementById( "gradientEyeTop" ).setAttribute( "stop-color", "#" + eye_1 );
					document.getElementById( "gradientEyeTop2" ).setAttribute( "stop-color", "#" + eye_1 );
					document.getElementById( "gradientEyeBottom" ).setAttribute( "stop-color", "#" + eye_2 );
					document.getElementById( "gradientEyeBottom2" ).setAttribute( "stop-color", "#" + eye_2 );*/
				}

				if ( typeof params[ "mouth" ] !== 'undefined' )
				{
					css += ".gMouth" + params[ "mouth" ] + " { display:inline; } ";
				}

				if ( typeof params[ "eye" ] !== 'undefined' )
				{
					css += ".gEye" + params[ "eye" ] + " { display:inline; } ";
				}

				if ( typeof params[ "faceaccessory" ] !== 'undefined' )
				{
					var parts = params[ "faceaccessory" ].split( ":" );
					css += ".gFaceAccessory" + parts[ 0 ] + " { display:inline; } ";

					if ( typeof parts[ 1 ] !== 'undefined' )
					{
						css += ".gFaceAccessory" + parts[ 0 ] + " .BaseFill { fill: #" + parts[ 1 ] + " !important; } ";
						css += ".gFaceAccessory" + parts[ 0 ] + " .BaseStroke { stroke: #" + parts[ 1 ] + " !important; } ";

						var outline = ColorMultiply( parts[ 1 ], 1 );
						css += ".gFaceAccessory" + parts[ 0 ] + " .OutlineFill { fill: #" + outline + " !important; } ";
						css += ".gFaceAccessory" + parts[ 0 ] + " .OutlineStroke { stroke: #" + outline + " !important; } ";
					}
				}

				if ( typeof params[ "accessory" ] !== 'undefined' )
				{
					var accessories = params[ "accessory" ].split( "," );

					for ( var a = 0; a < accessories.length; a++ )
					{
						var accessory_parts = accessories[ a ].split( ":" );
						css += ".gAccessory" + accessory_parts[ 0 ] + " { display:inline; } ";

						if ( typeof accessory_parts[ 1 ] !== 'undefined' )
						{
							css += ".gAccessory" + accessory_parts[ 0 ] + " .BaseFill { fill: #" + accessory_parts[ 1 ] + " !important; } ";
							css += ".gAccessory" + accessory_parts[ 0 ] + " .BaseStroke { stroke: #" + accessory_parts[ 1 ] + " !important; } ";

							var outline = ColorMultiply( accessory_parts[ 1 ], 1 );
							css += ".gAccessory" + accessory_parts[ 0 ] + " .OutlineFill { fill: #" + outline + " !important; } ";
							css += ".gAccessory" + accessory_parts[ 0 ] + " .OutlineStroke { stroke: #" + outline + " !important; } ";
						}
					}
				}

				if ( typeof params[ "horn" ] !== 'undefined' )
				{
					css += ".gHorn" + params[ "horn" ] + " { display:inline; } ";
				}

				if ( typeof params[ "mane" ] !== 'undefined' )
				{
					css += ".gMane" + params[ "mane" ] + " { display:inline; } ";

					if ( hairColor != null )
			    	{
			    		var hairColors = hairColor.split( "," );

			    		for ( var i = 0; i < 3; i++ )
			    		{
				    		var base = null;
				    		if ( typeof( hairColors[ i ] ) !== 'undefined' )
				    		{
				    			base = hairColors[i];
				    		}
				    		else
				    		{
				    			base = hairColors[0];
				    		}
				    		var outline = ColorMultiply( base, 1 );
							css += ".gMane" + params[ "mane" ] + " .BaseFill" + ( i + 1 ) + " { fill:#" + base + " !important; } ";
							css += ".gMane" + params[ "mane" ] + " .BaseStroke" + ( i + 1 ) + " { stroke:#" + base + " !important; } ";
							css += ".gMane" + params[ "mane" ] + " .OutlineFill" + ( i + 1 ) + " { fill:#" + outline + " !important; } ";
							css += ".gMane" + params[ "mane" ] + " .OutlineStroke" + ( i + 1 ) + " { stroke:#" + outline + " !important; } ";

							if ( i == 0 )
							{
								css += ".gMane" + params[ "mane" ] + " .BaseFill { fill:#" + base + " !important; } ";
								css += ".gMane" + params[ "mane" ] + " .BaseStroke { stroke:#" + base + " !important; } ";
								css += ".gMane" + params[ "mane" ] + " .OutlineFill { fill:#" + outline + " !important; } ";
								css += ".gMane" + params[ "mane" ] + " .OutlineStroke { stroke:#" + outline + " !important; } ";
							}
						}						
			    	}

					// Specific hair
					if ( params[ "mane" ] == 3 )
					{
						var highlight1 = ColorHSVShift( hairColor, ( 275.0 - 227.0 ) / 360.0, ( 66.0 - 65.0 ) / 100.0, ( 53.0 - 44.0 ) / 100.0 );
						var highlight2 = ColorHSVShift( hairColor, ( 334.0 - 227.0 ) / 360.0, ( 70.0 - 65.0 ) / 100.0, ( 90.0 - 44.0 ) / 100.0 );
						css += ".mane3Highlight1 { fill:#" + highlight1 + "; } ";
						css += ".mane3Highlight2 { fill:#" + highlight2 + "; } ";
					}
					if ( params[ "mane" ] == 6 )
					{
						var colour1 = ColorHSVShift( hairColor, ( 4.0 - 197.0 ) / 360.0, ( 79.0 - 99.0 ) / 100.0, ( 95.0 - 81.0 ) / 100.0 );
						var colour2 = ColorHSVShift( hairColor, ( 20.0 - 197.0 ) / 360.0, ( 79.0 - 99.0 ) / 100.0, ( 96.0 - 81.0 ) / 100.0 );
						var colour3 = ColorHSVShift( hairColor, ( 55.0 - 197.0 ) / 360.0, ( 49.0 - 99.0 ) / 100.0, ( 96.0 - 81.0 ) / 100.0 );
						var colour4 = ColorHSVShift( hairColor, ( 93.0 - 197.0 ) / 360.0, ( 67.0 - 99.0 ) / 100.0, ( 76.0 - 81.0 ) / 100.0 );
						var colour5 = hairColor;
						var colour6 = ColorHSVShift( hairColor, ( 277.0 - 197.0 ) / 360.0, ( 67.0 - 99.0 ) / 100.0, ( 54.0 - 81.0 ) / 100.0 );

						css += ".mane6Colour1 { fill:#" + colour1 + "; } ";
						css += ".mane6Colour2 { fill:#" + colour2 + "; } ";
						css += ".mane6Colour3 { fill:#" + colour3 + "; } ";
						css += ".mane6Colour4 { fill:#" + colour4 + "; } ";
						css += ".mane6Colour5 { fill:#" + colour5 + "; } ";
						css += ".mane6Colour6 { fill:#" + colour6 + "; } ";
					}
				}

				if ( typeof params[ "eyeBottomExpression" ] !== 'undefined' )
				{
					css += ".gEyeBottom" + params[ "eyeBottomExpression" ] + " { display:inline; } ";
				}
				else
				{
					css += ".gEyeBottom0 { display:inline; } ";
				}

				if ( typeof params[ "eyeLid" ] !== 'undefined' )
				{
					css += ".gEyeLid" + params[ "eyeLid" ] + " { display:inline; } ";
				}

				if ( typeof params[ "animation" ] !== 'undefined' )
				{
					clearInterval( timeout );

					if ( params[ "animation" ] == "shifty" )
						timeout = setInterval( EyesShifty, 33 );

					if ( params[ "animation" ] == "roll" )
						timeout = setInterval( EyesRoll, 33 );

					if ( params[ "animation"] == "headbang" )
					{
						var tags = document.getElementsByClassName( "gAnimationHeadBang" );
						for ( var t = 0; t < tags.length; t++ )
						{
							tags[ t ].beginElement();

							if ( typeof params[ "animationLength" ] !== 'undefined' )
							{
								tags[ t ].setAttribute( "dur", params[ "animationLength" ] + "s" );
							}
						}
					}
				}		


				//
				// Iris
				//

				eyeScale = params[ "irisS" ];
				SetIris( "Front", params[ "irisA" ] * 3.14159 / 180.0, params[ "irisR" ], 1 );
				SetIris( "Back", params[ "irisA" ] * 3.14159 / 180.0, params[ "irisR" ], 0.85 );

				if ( isIE )
			    {
					document.getElementById( "style" ).textContent = css;
				}
				else
				{
			    	document.getElementById( "style" ).innerHTML = css;
			    }

			    document.getElementById('svg2').forceRedraw( );

			   //Array.prototype.filter.call(document.getElementsByClassName("toggleable"), function(n){ return getComputedStyle( n ).display == "none"}).forEach(function(n){n.parentNode.removeChild(n)});

			}	
		}

		ProcessHash( document.location.hash );

		if (("onhashchange" in window) ) 
		{
	         window.onhashchange = function () {
	              ProcessHash(window.location.hash);
	         }
	    }
	    else {
	        var prevHash = window.location.hash;
	        window.setInterval(function () {
	           if (window.location.hash != prevHash) {
	              prevHash = window.location.hash;
	              ProcessHash(window.location.hash);
	           }
	        }, 100);
	    }

		function fixPageXY(e) 
		{
			if (e.pageX == null && e.clientX != null ) 
			{ 
				var html = document.documentElement
				var body = document.body

				e.pageX = e.clientX + (html.scrollLeft || body && body.scrollLeft || 0)
				e.pageX -= html.clientLeft || 0

				e.pageY = e.clientY + (html.scrollTop || body && body.scrollTop || 0)
				e.pageY -= html.clientTop || 0
			}
		}

		var mouse_down = false;

		function EyesLookAt( mouse_x, mouse_y )
		{
			{
				var iris_mask = document.getElementById( "EyeFrontIrisMask" );

				var x = parseInt( iris_mask.getAttribute( "cx" ) );
				var y = parseInt( iris_mask.getAttribute( "cy" ) );

				var dir = {}; dir.x = mouse_x - x; dir.y = mouse_y - y;

				var a = Math.atan2( dir.y, dir.x );

				SetIris( "Front", -a, 0.5, 1 );
			}

			{
				var iris_mask = document.getElementById( "EyeBackIrisMask" );

				var x = parseInt( iris_mask.getAttribute( "cx" ) );
				var y = parseInt( iris_mask.getAttribute( "cy" ) );

				var dir = {}; dir.x = mouse_x - x; dir.y = mouse_y - y;

				var a = Math.atan2( dir.y, dir.x );

				SetIris( "Back", -a, 0.5, 0.85 );
			}
		}

		document.onmousemove = function(e) 
		{
			if ( mouse_down )
			{
				e = e || window.event;
				//fixPageXY(e);


				EyesLookAt( e.pageX + 20, e.pageY );
			}
		}

		document.onmousedown = function(e) 
		{
			mouse_down = true;
		};

		document.onmouseup = function(e) 
		{
			mouse_down = false;
		};

		document.addEventListener('touchstart', function(e){
			mouse_down = true;
		}, false)

		document.addEventListener('touchmove', function(e){
			var touchobj = e.changedTouches[0] // reference first touch point for this event

			if ( mouse_down )
			{
				EyesLookAt( touchobj.clientX, touchobj.clientY );
			}
		}, false)

		document.addEventListener('touchend', function(e){
			mouse_down = false;
		}, false)

		window.addEventListener("message", receiveMessage, false);

		function receiveMessage(event)
		{
			ProcessHash( event.data );
		}
